import React, {Component} from 'react';
import axios from 'axios';

export default class DeleteTodo extends Component {

    constructor(props) {
        super(props);

        this.onChangeToDelete = this.onChangeToDelete.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            todo_description: '',
            todo_responsible: '',
            todo_priority: '',
            todo_completed: false, 
            toDelete: false
        }
    }

    componentDidMount() {
        axios.get('http://localhost:4000/todos/'+this.props.match.params.id)
            .then(response => {
                this.setState({
                    todo_description: response.data.todo_description,
                    todo_responsible: response.data.todo_responsible,
                    todo_priority: response.data.todo_priority,
                    todo_completed: response.data.todo_completed
                })
            })
            .catch(function(error) {
                console.log(error)
            })
    }

    onChangeToDelete(e) {
        this.setState({
            toDelete: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();
        if (this.state.toDelete) {
            axios.delete('http://localhost:4000/todos/delete/'+this.props.match.params.id)
            .then(res => console.log(res.data));
        }
        this.props.history.push('/');
    }

    render() {
        return (
            <div>
                <h3>Delete Todo</h3>
                <h5> Are you sure you want to delete the following todo? </h5>
                <div>
                    <span> Description: {this.state.todo_description} </span>
                    <span> Responsible: {this.state.todo_responsible} </span>
                    <span> Priority: {this.state.todo_priority} </span>
                    <span> Completed: {this.state.todo_completed} </span>
                </div>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input"
                                    type="radio"
                                    name="deleteOptions"
                                    id="No"
                                    value="No"
                                    checked={this.state.toDelete==='No'}
                                    onChange={this.onChangeToDelete}
                                    />
                            <label className="form-check-label">Low</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input"
                                    type="radio"
                                    name="deleteOptions"
                                    id="Yes"
                                    value="Yes"
                                    checked={this.state.toDelete==='Yes'}
                                    onChange={this.onChangeToDelete}
                                    />
                            <label className="form-check-label">Medium</label>
                        </div>
                        <br/>
                        <div className="form-group">
                            <input type="submit" value="Delete Todo" className="btn btn-primary" />
                        </div>
                    </div>
                </form>
            </div>
        )
    }
}
